import React, { useContext, useState } from "react";
import { WorkerContext } from "../context/WorkerContext";
import { Link } from "react-router-dom";
import Navbar from "../components/Navbar";

const WorkerList = () => {
  const { workers } = useContext(WorkerContext);
  const [search, setSearch] = useState("");
  const [filterProfession, setFilterProfession] = useState("");

  const filteredWorkers = workers.filter((w) => {
    return (
      w.name.toLowerCase().includes(search.toLowerCase()) &&
      (filterProfession ? w.profession === filterProfession : true)
    );
  });

  const professions = [...new Set(workers.map((w) => w.profession))];

  return (
    <>
      <Navbar />
      <div style={{ padding: "20px" }}>
        <h2>Available Workers</h2>

        <input
          type="text"
          placeholder="Search by name"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          style={{ padding: "5px", marginRight: "10px" }}
        />

        <select
          value={filterProfession}
          onChange={(e) => setFilterProfession(e.target.value)}
          style={{ padding: "5px" }}
        >
          <option value="">All Professions</option>
          {professions.map((p, idx) => (
            <option key={idx} value={p}>{p}</option>
          ))}
        </select>

        <div style={{ marginTop: "20px" }}>
          {filteredWorkers.length === 0 ? (
            <p>No workers available</p>
          ) : (
            filteredWorkers.map((w) => (
              <div key={w.id} style={{ border: "1px solid #ccc", margin: "10px", padding: "10px" }}>
                <h3>{w.name}</h3>
                <p>Profession: {w.profession}</p>
                <p>Hourly Rate: â‚¹{w.price}</p>
                <Link to={`/booking/${w.id}`}>
                  <button>Book Now</button>
                </Link>
              </div>
            ))
          )}
        </div>
      </div>
    </>
  );
};

export default WorkerList;
