import React, { useContext } from "react";
import Navbar from "../components/Navbar";
import { WorkerContext } from "../context/WorkerContext";
import { Link } from "react-router-dom";

const Home = () => {
  const { workers } = useContext(WorkerContext);
  const featuredWorkers = workers.slice(0, 3); // show first 3 as featured

  return (
    <>
      <Navbar />
      <div style={{ textAlign: "center", marginTop: "30px" }}>
        <h1>Welcome to UrbanWorkers</h1>
        <p>Find trusted workers for your daily tasks</p>
        <Link to="/workers">
          <button style={{ padding: "10px 20px", marginTop: "20px" }}>View All Workers</button>
        </Link>
      </div>

      <div style={{ marginTop: "50px", padding: "0 20px" }}>
        <h2>Featured Workers</h2>
        <div style={{ display: "flex", gap: "20px", flexWrap: "wrap" }}>
          {featuredWorkers.length === 0 && <p>No workers yet. Add one!</p>}
          {featuredWorkers.map((w) => (
            <div
              key={w.id}
              style={{
                border: "1px solid #ccc",
                borderRadius: "8px",
                padding: "15px",
                width: "250px",
              }}
            >
              <h3>{w.name}</h3>
              <p>Profession: {w.profession}</p>
              <p>Hourly Rate: â‚¹{w.price}</p>
              <Link to={`/booking/${w.id}`}>
                <button style={{ padding: "5px 10px" }}>Book Now</button>
              </Link>
            </div>
          ))}
        </div>
      </div>
    </>
  );
};

export default Home;
